        id: 8,
        name: 'Alex Thompson',
        email: 'alex.thompson@email.com',
        position: 'Mobile Developer',
        experience: '3 years',
        location: 'Denver, CO',
        skills: ['React Native', 'iOS', 'Android', 'Swift'],
        status: 'interviewed',
        lastContact: new Date('2024-01-17')
      }
    ];
  }

  toggleSelection(jobSeekerId: number): void {
    if (this.selectedJobSeekers.has(jobSeekerId)) {
      this.selectedJobSeekers.delete(jobSeekerId);
    } else {
      this.selectedJobSeekers.add(jobSeekerId);
    }
    this.updateSelectAllState();
  }

  toggleSelectAll(): void {
    if (this.selectAll) {
      this.selectedJobSeekers.clear();
    } else {
      this.jobSeekers.forEach(seeker => {
        this.selectedJobSeekers.add(seeker.id);
      });
    }
    this.selectAll = !this.selectAll;
  }

  private updateSelectAllState(): void {
    this.selectAll = this.selectedJobSeekers.size === this.jobSeekers.length;
  }

  getSelectedJobSeekers(): JobSeeker[] {
    return this.jobSeekers.filter(seeker => this.selectedJobSeekers.has(seeker.id));
  }

  createCampaign(): void {
    const selectedSeekers = this.getSelectedJobSeekers();
    if (selectedSeekers.length === 0) {
      alert('Please select at least one job seeker to create a campaign.');
      return;
    }

    // Navigate to campaign creation page with selected job seekers
    this.router.navigate(['/campaign'], { 
      state: { selectedJobSeekers: selectedSeekers } 
    });
  }

  getStatusClass(status: string): string {
    switch (status) {
      case 'active': return 'status-active';
      case 'contacted': return 'status-contacted';
      case 'interviewed': return 'status-interviewed';
      case 'hired': return 'status-hired';
      default: return 'status-active';
    }
  }

  getStatusLabel(status: string): string {
    switch (status) {
      case 'active': return 'Active';
      case 'contacted': return 'Contacted';
      case 'interviewed': return 'Interviewed';
      case 'hired': return 'Hired';
      default: return 'Active';
    }
  }

  trackBySeekerId(_: number, seeker: JobSeeker): number {
    return seeker.id;
  }
}
<div class="page">
  <div class="header card">
    <div class="section-header">
      <div>
        <h1>Job Seekers</h1>
        <p>Select job seekers to create personalized drip campaigns</p>
      </div>
      <div class="action-buttons">
        <button 
          class="btn btn-primary" 
          type="button" 
          (click)="createCampaign()"
          [disabled]="selectedJobSeekers.size === 0">
          <span class="icon">ðŸ“§</span>
          Create Campaign ({{ selectedJobSeekers.size }})
        </button>
      </div>
    </div>
  </div>

  <div class="card">
    <div class="table-header">
      <div class="select-all-container">
        <label class="checkbox-label">
          <input 
            type="checkbox" 
            [checked]="selectAll" 
            (change)="toggleSelectAll()">
          <span class="checkmark"></span>
          Select All
        </label>
      </div>
      <div class="table-info">
        Showing {{ jobSeekers.length }} job seekers
      </div>
    </div>

    <div class="table-container">
      <table class="job-seekers-table">
        <thead>
          <tr>
            <th class="checkbox-column"></th>
            <th>Name</th>
            <th>Position</th>
            <th>Experience</th>
            <th>Location</th>
            <th>Skills</th>
            <th>Status</th>
            <th>Last Contact</th>
          </tr>
        </thead>
        <tbody>
          <tr 
            *ngFor="let seeker of jobSeekers; trackBy: trackBySeekerId" 
            class="table-row"
            [ngClass]="{ 'selected': selectedJobSeekers.has(seeker.id) }">

            <td class="checkbox-column">
              <label class="checkbox-label">
                <input 
                  type="checkbox" 
                  [checked]="selectedJobSeekers.has(seeker.id)"
                  (change)="toggleSelection(seeker.id)">
                <span class="checkmark"></span>
              </label>
            </td>

            <td class="name-column">
              <div class="seeker-info">
                <div class="seeker-name">{{ seeker.name }}</div>
                <div class="seeker-email">{{ seeker.email }}</div>
              </div>
            </td>

            <td class="position-column">{{ seeker.position }}</td>

            <td class="experience-column">{{ seeker.experience }}</td>

            <td class="location-column">{{ seeker.location }}</td>

            <td class="skills-column">
              <div class="skills-container">
                <span 
                  *ngFor="let skill of seeker.skills.slice(0, 3)" 
                  class="skill-tag">
                  {{ skill }}
                </span>
                <span 
                  *ngIf="seeker.skills.length > 3" 
                  class="skill-more">
                  +{{ seeker.skills.length - 3 }}
                </span>
              </div>
            </td>

            <td class="status-column">
              <span class="status-badge" [ngClass]="getStatusClass(seeker.status)">
                {{ getStatusLabel(seeker.status) }}
              </span>
            </td>

            <td class="last-contact-column">
              {{ seeker.lastContact | date:'MMM dd, yyyy' }}
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>
