import { CommonModule, NgClass, NgFor, NgIf } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormsModule } from '@angular/forms';

import { CrmService, DripStep, EmailTemplate, TimeIntervalOption } from './crm.service';

interface TemplateFormState {
  name: string;
  description: string;
  content: string;
}

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule, NgFor, NgIf, NgClass],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  readonly title = 'Drip Campaign Builder';

  emailTemplates: EmailTemplate[] = [];
  timeIntervals: TimeIntervalOption[] = [];

  numDrips = 3;
  drips: DripStep[] = [
    { id: 1, template: '', interval: 0, customInterval: 1 },
    { id: 2, template: '', interval: 3, customInterval: 3 },
    { id: 3, template: '', interval: 7, customInterval: 7 }
  ];
  globalInterval = 3;
  campaignName = '';

  showTemplateModal = false;
  showCreateModal = false;
  selectedTemplatePreview: string | null = null;

  templateForm: TemplateFormState = {
    name: '',
    description: '',
    content: ''
  };

  statusMessage: string | null = null;
  statusType: 'success' | 'error' | null = null;
  isSaving = false;

  constructor(private readonly crmService: CrmService) {}

  ngOnInit(): void {
    this.fetchTemplates();
    this.fetchTimeIntervals();
  }

  get totalDays(): number {
    return this.drips.reduce((sum, drip) => sum + (drip.id === 1 ? 0 : drip.interval), 0);
  }

  get configuredDrips(): number {
    return this.drips.filter((drip) => Boolean(drip.template)).length;
  }

  private fetchTemplates(): void {
    this.crmService.getTemplates().subscribe({
      next: (templates) => {
        this.emailTemplates = templates;
      },
      error: () => {
        // Fallback to local defaults if API is unavailable
        this.emailTemplates = [
          { id: 'welcome', name: 'Welcome Email', description: 'Introduce your brand', content: '' },
          { id: 'intro', name: 'Product Introduction', description: 'Showcase your product', content: '' },
          { id: 'case_study', name: 'Case Study', description: 'Share success stories', content: '' }
        ];
      }
    });
  }

  private fetchTimeIntervals(): void {
    this.crmService.getTimeIntervals().subscribe({
      next: (intervals) => {
        this.timeIntervals = intervals;
      },
      error: () => {
        this.timeIntervals = [
          { value: 1, label: '1 day' },
          { value: 3, label: '3 days' },
          { value: 7, label: '1 week' }
        ];
      }
    });
  }

  handleDripCountChange(value: string | number): void {
    const newCount = Math.max(1, Number(value) || 1);
    this.numDrips = newCount;

    if (newCount > this.drips.length) {
      const newDrips = [...this.drips];
      for (let i = this.drips.length; i < newCount; i += 1) {
        newDrips.push({
          id: i + 1,
          template: '',
          interval: i === 0 ? 0 : this.globalInterval,
          customInterval: this.globalInterval
        });
      }
      this.drips = newDrips;
    } else if (newCount < this.drips.length) {
      this.drips = this.drips.slice(0, newCount).map((drip, index) => ({
        ...drip,
        id: index + 1
      }));
    }
  }

  updateDripTemplate(id: number, templateId: string): void {
    this.drips = this.drips.map((drip) =>
      drip.id === id ? { ...drip, template: templateId } : drip
    );
  }

  updateDripInterval(id: number, interval: number): void {
    this.drips = this.drips.map((drip) =>
      drip.id === id ? { ...drip, interval, customInterval: interval } : drip
    );
  }

  addDrip(): void {
    const nextId = this.drips.length + 1;
    this.drips = [
      ...this.drips,
      {
        id: nextId,
        template: '',
        interval: this.globalInterval,
        customInterval: this.globalInterval
      }
    ];
    this.numDrips = nextId;
  }

  removeDrip(id: number): void {
    if (this.drips.length <= 1) {
      return;
    }

    this.drips = this.drips
      .filter((drip) => drip.id !== id)
      .map((drip, index) => ({ ...drip, id: index + 1 }));
    this.numDrips = this.drips.length;
  }

  applyGlobalInterval(): void {
    this.drips = this.drips.map((drip, index) => ({
      ...drip,
      interval: index === 0 ? 0 : this.globalInterval,
      customInterval: index === 0 ? 0 : this.globalInterval
    }));
  }

  openTemplateModal(): void {
    this.showTemplateModal = true;
  }

  closeTemplateModal(): void {
    this.showTemplateModal = false;
  }

  openCreateModal(): void {
    this.resetTemplateForm();
    this.showCreateModal = true;
  }

  closeCreateModal(): void {
    this.showCreateModal = false;
  }

  previewTemplate(templateId: string): void {
    this.selectedTemplatePreview = templateId;
  }

  closePreview(): void {
    this.selectedTemplatePreview = null;
  }

  saveCampaign(): void {
    this.isSaving = true;
    this.statusMessage = null;

    const payload = {
      name: this.campaignName || 'Untitled Campaign',
      drips: this.drips.map((drip) => ({
        id: drip.id,
        template: drip.template,
        interval: drip.id === 1 ? 0 : drip.interval
      }))
    };

    this.crmService.saveCampaign(payload).subscribe({
      next: () => {
        this.isSaving = false;
        this.statusType = 'success';
        this.statusMessage = 'Campaign saved successfully!';
      },
      error: () => {
        this.isSaving = false;
        this.statusType = 'error';
        this.statusMessage = 'Unable to save campaign. Please try again later.';
      }
    });
  }

  createTemplate(): void {
    if (!this.templateForm.name || !this.templateForm.description || !this.templateForm.content) {
      this.statusType = 'error';
      this.statusMessage = 'Please fill out all template fields before submitting.';
      return;
    }

    this.crmService.createTemplate(this.templateForm).subscribe({
      next: (template) => {
        this.emailTemplates = [...this.emailTemplates, template];
        this.showCreateModal = false;
        this.statusType = 'success';
        this.statusMessage = 'Template created successfully!';
        this.resetTemplateForm();
      },
      error: () => {
        this.statusType = 'error';
        this.statusMessage = 'Unable to create template. Please try again later.';
      }
    });
  }

  trackByDripId(_: number, drip: DripStep): number {
    return drip.id;
  }

  getTemplateName(templateId: string): string {
    const template = this.emailTemplates.find(t => t.id === templateId);
    return template?.name || 'Unknown Template';
  }

  getTemplateDescription(templateId: string): string {
    const template = this.emailTemplates.find(t => t.id === templateId);
    return template?.description || '';
  }

  getTemplateContent(templateId: string): string {
    const template = this.emailTemplates.find(t => t.id === templateId);
    return template?.content || '';
  }

  private resetTemplateForm(): void {
    this.templateForm = {
      name: '',
      description: '',
      content: ''
    };
  }
}
<div class="page">
  <div class="header card">
    <div class="section-header">
      <div>
        <h1>{{ title }}</h1>
        <p>Configure your automated email sequence</p>
      </div>
      <div class="action-buttons">
        <button class="btn btn-success" type="button" (click)="openCreateModal()">
          <span class="icon">➕</span>
          Create Template
        </button>
        <button class="btn btn-primary" type="button" (click)="openTemplateModal()">
          <span class="icon">👁️</span>
          View Templates
        </button>
      </div>
    </div>
    <p class="status" *ngIf="statusMessage" [ngClass]="{ success: statusType === 'success', error: statusType === 'error' }">
      {{ statusMessage }}
    </p>
  </div>

  <div class="layout">
    <div class="card sticky-panel">
      <div class="section-title">General Settings</div>

      <div class="form-group">
        <label for="campaignName">Campaign Name</label>
        <input
          id="campaignName"
          name="campaignName"
          type="text"
          [(ngModel)]="campaignName"
          placeholder="e.g., Welcome Series 2024"
        />
      </div>

      <div class="form-group">
        <label for="numDrips">Number of Drip Emails</label>
        <div class="inline-group">
          <input
            id="numDrips"
            type="number"
            min="1"
            [ngModel]="numDrips"
            (ngModelChange)="handleDripCountChange($event)"
          />
          <button class="btn btn-primary" type="button" (click)="addDrip()">➕</button>
        </div>
      </div>

      <div class="form-group info-box">
        <label for="globalInterval">Send After Previous Email (Global)</label>
        <select
          id="globalInterval"
          [(ngModel)]="globalInterval"
        >
          <option *ngFor="let interval of timeIntervals" [ngValue]="interval.value">
            {{ interval.label }}
          </option>
        </select>
        <button class="btn btn-primary full" type="button" (click)="applyGlobalInterval()">
          Apply to All Drips
        </button>
        <p class="help-text">Set a default interval for all emails (except the first)</p>
      </div>

      <div class="stat-box">
        <div class="stat-row">
          <span>Total Emails:</span>
          <span>{{ drips.length }}</span>
        </div>
        <div class="stat-row">
          <span>Duration:</span>
          <span>~{{ totalDays }} days</span>
        </div>
        <div class="stat-row">
          <span>Configured:</span>
          <span>{{ configuredDrips }}/{{ drips.length }}</span>
        </div>
      </div>

      <button class="btn btn-success full" type="button" (click)="saveCampaign()" [disabled]="isSaving">
        {{ isSaving ? 'Saving...' : 'Save Campaign' }}
      </button>
    </div>

    <div class="card">
      <div class="section-title">Email Workflows</div>

      <div class="workflow-list">
        <div
          class="workflow-wrapper"
          *ngFor="let drip of drips; let index = index; trackBy: trackByDripId"
        >
          <div class="workflow-card">
            <div class="workflow-header">
              <div class="workflow-meta">
                <div class="badge">{{ index + 1 }}</div>
                <div>
                  <h3>Email #{{ index + 1 }}</h3>
                  <span *ngIf="index === 0">Sends immediately</span>
                </div>
              </div>
              <button class="btn btn-danger" type="button" (click)="removeDrip(drip.id)" *ngIf="drips.length > 1">
                ✕
              </button>
            </div>

            <div class="grid-two">
              <div *ngIf="index > 0">
                <label>Wait Time</label>
                <select [ngModel]="drip.interval" (ngModelChange)="updateDripInterval(drip.id, $event)">
                  <option *ngFor="let interval of timeIntervals" [ngValue]="interval.value">
                    {{ interval.label }}
                  </option>
                </select>
              </div>

              <div [ngClass]="{ 'full-span': index === 0 }">
                <label>Email Template</label>
                <select [ngModel]="drip.template" (ngModelChange)="updateDripTemplate(drip.id, $event)">
                  <option value="">Select template...</option>
                  <option *ngFor="let template of emailTemplates" [ngValue]="template.id">
                    {{ template.name }}
                  </option>
                </select>
              </div>
            </div>

            <div class="preview-card" *ngIf="drip.template">
              <div class="preview-header">
                <div>
                  <strong>{{ getTemplateName(drip.template) }}</strong>
                </div>
                <button type="button" class="link" (click)="previewTemplate(drip.template)">
                  Preview
                </button>
              </div>
              <p>{{ getTemplateDescription(drip.template) }}</p>
            </div>
          </div>

          <div class="connector" *ngIf="index < drips.length - 1">
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
              <path d="M19 14l-7 7-7-7M12 21V3" />
            </svg>
          </div>
        </div>
      </div>
    </div>
  </div>

  <div class="modal-backdrop" *ngIf="showTemplateModal">
    <div class="modal-panel">
      <div class="modal-header">
        <h2>Email Templates</h2>
        <button type="button" class="btn btn-danger" (click)="closeTemplateModal()">✕</button>
      </div>
      <div class="modal-content">
        <div class="template-card" *ngFor="let template of emailTemplates">
          <div class="template-header">
            <div>
              <h3>{{ template.name }}</h3>
              <p>{{ template.description }}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

  <div class="modal-backdrop" *ngIf="showCreateModal">
    <div class="modal-panel">
      <div class="modal-header">
        <h2>Create New Template</h2>
        <button type="button" class="btn btn-danger" (click)="closeCreateModal()">✕</button>
      </div>
      <div class="modal-content">
        <div class="form-group">
          <label for="templateName">Template Name</label>
          <input id="templateName" type="text" [(ngModel)]="templateForm.name" placeholder="e.g., Product Launch Email" />
        </div>
        <div class="form-group">
          <label for="templateDescription">Description</label>
          <input
            id="templateDescription"
            type="text"
            [(ngModel)]="templateForm.description"
            placeholder="Brief description of this template"
          />
        </div>
        <div class="form-group">
          <label for="templateContent">Email Content</label>
          <textarea
            id="templateContent"
            rows="6"
            [(ngModel)]="templateForm.content"
            placeholder="Write your email content here..."
          ></textarea>
        </div>
        <div class="modal-actions">
          <button type="button" class="btn" (click)="closeCreateModal()">Cancel</button>
          <button type="button" class="btn btn-success" (click)="createTemplate()">Create Template</button>
        </div>
      </div>
    </div>
  </div>

  <div class="modal-backdrop" *ngIf="selectedTemplatePreview as templateId">
    <div class="modal-panel">
      <div class="modal-header">
        <h2>{{ getTemplateName(templateId) }}</h2>
        <button type="button" class="btn btn-danger" (click)="closePreview()">✕</button>
      </div>
      <div class="modal-content">
        <p>{{ getTemplateDescription(templateId) }}</p>
        <div class="preview-body">
          {{ getTemplateContent(templateId) || 'Template content preview not available.' }}
        </div>
      </div>
    </div>
  </div>
</div>
