import { CommonModule, NgClass, NgFor, NgIf } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';

import { CrmService, DripStep, EmailTemplate, TimeIntervalOption } from './crm.service';

interface TemplateFormState {
  name: string;
  description: string;
  content: string;
}

@Component({
  selector: 'app-campaign',
  standalone: true,
  imports: [CommonModule, FormsModule, NgFor, NgIf, NgClass],
  templateUrl: './campaign.component.html',
  styleUrls: ['./campaign.component.css']
})
export class CampaignComponent implements OnInit {
  readonly title = 'Drip Campaign Builder';

  emailTemplates: EmailTemplate[] = [];
  timeIntervals: TimeIntervalOption[] = [];

  numDrips = 3;
  drips: DripStep[] = [
    { id: 1, template: '', interval: 0, customInterval: 1 },
    { id: 2, template: '', interval: 3, customInterval: 3 },
    { id: 3, template: '', interval: 7, customInterval: 7 }
  ];
  globalInterval = 3;
  campaignName = '';

  showTemplateModal = false;
  showCreateModal = false;
  selectedTemplatePreview: string | null = null;

  templateForm: TemplateFormState = {
    name: '',
    description: '',
    content: ''
  };

  statusMessage: string | null = null;
  statusType: 'success' | 'error' | null = null;
  isSaving = false;

  constructor(private readonly crmService: CrmService, private router: Router) {}

  ngOnInit(): void {
    this.fetchTemplates();
    this.fetchTimeIntervals();
  }

  get totalDays(): number {
    return this.drips.reduce((sum, drip) => sum + (drip.id === 1 ? 0 : drip.interval), 0);
  }

  get configuredDrips(): number {
    return this.drips.filter((drip) => Boolean(drip.template)).length;
  }

  private fetchTemplates(): void {
    this.crmService.getTemplates().subscribe({
      next: (templates) => {
        this.emailTemplates = templates;
      },
      error: () => {
        // Fallback to local defaults if API is unavailable
        this.emailTemplates = [
          { id: 'welcome', name: 'Welcome Email', description: 'Introduce your brand', content: '' },
          { id: 'intro', name: 'Product Introduction', description: 'Showcase your product', content: '' },
          { id: 'case_study', name: 'Case Study', description: 'Share success stories', content: '' }
        ];
      }
    });
  }

  private fetchTimeIntervals(): void {
    this.crmService.getTimeIntervals().subscribe({
      next: (intervals) => {
        this.timeIntervals = intervals;
      },
      error: () => {
        this.timeIntervals = [
          { value: 1, label: '1 day' },
          { value: 3, label: '3 days' },
          { value: 7, label: '1 week' }
        ];
      }
    });
  }

  handleDripCountChange(value: string | number): void {
    const newCount = Math.max(1, Number(value) || 1);
    this.numDrips = newCount;

    if (newCount > this.drips.length) {
      const newDrips = [...this.drips];
      for (let i = this.drips.length; i < newCount; i += 1) {
        newDrips.push({
          id: i + 1,
          template: '',
          interval: i === 0 ? 0 : this.globalInterval,
          customInterval: this.globalInterval
        });
      }
      this.drips = newDrips;
    } else if (newCount < this.drips.length) {
      this.drips = this.drips.slice(0, newCount).map((drip, index) => ({
        ...drip,
        id: index + 1
      }));
    }
  }

  updateDripTemplate(id: number, templateId: string): void {
    this.drips = this.drips.map((drip) =>
      drip.id === id ? { ...drip, template: templateId } : drip
    );
  }

  updateDripInterval(id: number, interval: number): void {
    this.drips = this.drips.map((drip) =>
      drip.id === id ? { ...drip, interval, customInterval: interval } : drip
    );
  }

  addDrip(): void {
    const nextId = this.drips.length + 1;
    this.drips = [
      ...this.drips,
      {
        id: nextId,
        template: '',
        interval: this.globalInterval,
        customInterval: this.globalInterval
      }
    ];
    this.numDrips = nextId;
  }

  removeDrip(id: number): void {
    if (this.drips.length <= 1) {
      return;
    }

    this.drips = this.drips
      .filter((drip) => drip.id !== id)
      .map((drip, index) => ({ ...drip, id: index + 1 }));
    this.numDrips = this.drips.length;
  }

  applyGlobalInterval(): void {
    this.drips = this.drips.map((drip, index) => ({
      ...drip,
      interval: index === 0 ? 0 : this.globalInterval,
      customInterval: index === 0 ? 0 : this.globalInterval
    }));
  }

  openTemplateModal(): void {
    this.showTemplateModal = true;
  }

  closeTemplateModal(): void {
    this.showTemplateModal = false;
  }

  openCreateModal(): void {
    this.resetTemplateForm();
    this.showCreateModal = true;
  }

  closeCreateModal(): void {
    this.showCreateModal = false;
  }

  previewTemplate(templateId: string): void {
    this.selectedTemplatePreview = templateId;
  }

  closePreview(): void {
    this.selectedTemplatePreview = null;
  }

  saveCampaign(): void {
    this.isSaving = true;
    this.statusMessage = null;

    const payload = {
      name: this.campaignName || 'Untitled Campaign',
      drips: this.drips.map((drip) => ({
        id: drip.id,
        template: drip.template,
        interval: drip.id === 1 ? 0 : drip.interval
      }))
    };

    this.crmService.saveCampaign(payload).subscribe({
      next: () => {
        this.isSaving = false;
        this.statusType = 'success';
        this.statusMessage = 'Campaign saved successfully!';
      },
      error: () => {
        this.isSaving = false;
        this.statusType = 'error';
        this.statusMessage = 'Unable to save campaign. Please try again later.';
      }
    });
  }

  createTemplate(): void {
    if (!this.templateForm.name || !this.templateForm.description || !this.templateForm.content) {
      this.statusType = 'error';
      this.statusMessage = 'Please fill out all template fields before submitting.';
      return;
    }

    this.crmService.createTemplate(this.templateForm).subscribe({
      next: (template) => {
        this.emailTemplates = [...this.emailTemplates, template];
        this.showCreateModal = false;
        this.statusType = 'success';
        this.statusMessage = 'Template created successfully!';
        this.resetTemplateForm();
      },
      error: () => {
        this.statusType = 'error';
        this.statusMessage = 'Unable to create template. Please try again later.';
      }
    });
  }

  trackByDripId(_: number, drip: DripStep): number {
    return drip.id;
  }

  getTemplateName(templateId: string): string {
    const template = this.emailTemplates.find(t => t.id === templateId);
    return template?.name || 'Unknown Template';
  }

  getTemplateDescription(templateId: string): string {
    const template = this.emailTemplates.find(t => t.id === templateId);
    return template?.description || '';
  }

  getTemplateContent(templateId: string): string {
    const template = this.emailTemplates.find(t => t.id === templateId);
    return template?.content || '';
  }

  goBackToJobSeekers(): void {
    this.router.navigate(['/']);
  }

  private resetTemplateForm(): void {
    this.templateForm = {
      name: '',
      description: '',
      content: ''
    };
  }
}
<div class="campaign-page">
  <div class="page-header">
    <div class="header-content">
      <div class="header-info">
        <div class="breadcrumb">
          <a href="#" (click)="goBackToJobSeekers(); $event.preventDefault()" class="breadcrumb-link">
            <svg class="breadcrumb-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 19l-7-7 7-7"></path>
            </svg>
            Job Seekers
          </a>
          <svg class="breadcrumb-separator" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5l7 7-7 7"></path>
          </svg>
          <span class="breadcrumb-current">Campaign Builder</span>
        </div>
        <h1 class="page-title">
          <svg class="title-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z"></path>
          </svg>
          {{ title }}
        </h1>
        <p class="page-description">Create personalized drip email campaigns for your selected job seekers</p>
      </div>
      <div class="header-actions">
        <button class="btn btn-outline" type="button" (click)="openTemplateModal()">
          <svg class="btn-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"></path>
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z"></path>
          </svg>
          View Templates
        </button>
        <button class="btn btn-primary" type="button" (click)="openCreateModal()">
          <svg class="btn-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6"></path>
          </svg>
          Create Template
        </button>
      </div>
    </div>
    <div class="status-container" *ngIf="statusMessage">
      <div class="status-message" [ngClass]="{ 'status-success': statusType === 'success', 'status-error': statusType === 'error' }">
        <svg class="status-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24" *ngIf="statusType === 'success'">
          <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 13l4 4L19 7"></path>
        </svg>
        <svg class="status-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24" *ngIf="statusType === 'error'">
          <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"></path>
        </svg>
        {{ statusMessage }}
      </div>
    </div>
  </div>

  <div class="campaign-layout">
    <div class="settings-panel">
      <div class="panel-header">
        <div class="panel-title">
          <svg class="panel-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z"></path>
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"></path>
          </svg>
          Campaign Settings
        </div>
      </div>

      <div class="form-section">
        <div class="form-group">
          <label for="campaignName" class="form-label">
            <svg class="label-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M7 7h.01M7 3h5c.512 0 1.024.195 1.414.586l7 7a2 2 0 010 2.828l-7 7a2 2 0 01-2.828 0l-7-7A1.994 1.994 0 013 12V7a4 4 0 014-4z"></path>
            </svg>
            Campaign Name
          </label>
          <input
            id="campaignName"
            name="campaignName"
            type="text"
            [(ngModel)]="campaignName"
            placeholder="e.g., Welcome Series 2024"
            class="form-input"
          />
        </div>

        <div class="form-group">
          <label for="numDrips" class="form-label">
            <svg class="label-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2"></path>
            </svg>
            Number of Emails
          </label>
          <div class="input-group">
            <input
              id="numDrips"
              type="number"
              min="1"
              [ngModel]="numDrips"
              (ngModelChange)="handleDripCountChange($event)"
              class="form-input"
            />
            <button class="btn btn-icon-only" type="button" (click)="addDrip()" title="Add Email">
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6"></path>
              </svg>
            </button>
          </div>
        </div>

        <div class="form-group global-settings">
          <label for="globalInterval" class="form-label">
            <svg class="label-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"></path>
            </svg>
            Default Wait Time
          </label>
          <select
            id="globalInterval"
            [(ngModel)]="globalInterval"
            class="form-select"
          >
            <option *ngFor="let interval of timeIntervals" [ngValue]="interval.value">
              {{ interval.label }}
            </option>
          </select>
          <button class="btn btn-secondary" type="button" (click)="applyGlobalInterval()">
            <svg class="btn-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"></path>
            </svg>
            Apply to All
          </button>
          <p class="help-text">Set default interval for all emails (except the first)</p>
        </div>
      </div>

      <div class="stats-section">
        <div class="stats-grid">
          <div class="stat-item">
            <div class="stat-value">{{ drips.length }}</div>
            <div class="stat-label">Total Emails</div>
          </div>
          <div class="stat-item">
            <div class="stat-value">~{{ totalDays }}</div>
            <div class="stat-label">Days Duration</div>
          </div>
          <div class="stat-item">
            <div class="stat-value">{{ configuredDrips }}/{{ drips.length }}</div>
            <div class="stat-label">Configured</div>
          </div>
        </div>
      </div>

      <div class="panel-footer">
        <button class="btn btn-success btn-large" type="button" (click)="saveCampaign()" [disabled]="isSaving">
          <svg class="btn-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24" *ngIf="!isSaving">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 13l4 4L19 7"></path>
          </svg>
          <svg class="btn-icon spinning" fill="none" stroke="currentColor" viewBox="0 0 24 24" *ngIf="isSaving">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"></path>
          </svg>
          {{ isSaving ? 'Saving Campaign...' : 'Save Campaign' }}
        </button>
      </div>
    </div>

    <div class="workflows-panel">
      <div class="panel-header">
        <div class="panel-title">
          <svg class="panel-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2"></path>
          </svg>
          Email Workflows
        </div>
        <div class="workflow-count">{{ drips.length }} Email{{ drips.length !== 1 ? 's' : '' }}</div>
      </div>

      <div class="workflow-timeline">
        <div
          class="workflow-step"
          *ngFor="let drip of drips; let index = index; trackBy: trackByDripId"
        >
          <div class="step-indicator">
            <div class="step-number">{{ index + 1 }}</div>
            <div class="step-line" *ngIf="index < drips.length - 1"></div>
          </div>

          <div class="workflow-card">
            <div class="workflow-header">
              <div class="workflow-info">
                <h3 class="workflow-title">
                  Email #{{ index + 1 }}
                  <span class="workflow-badge" *ngIf="drip.template">Configured</span>
                </h3>
                <p class="workflow-subtitle" *ngIf="index === 0">
                  <svg class="inline-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M13 10V3L4 14h7v7l9-11h-7z"></path>
                  </svg>
                  Sends immediately
                </p>
                <p class="workflow-subtitle" *ngIf="index > 0">
                  <svg class="inline-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"></path>
                  </svg>
                  Waits {{ getIntervalLabel(drip.interval) }}
                </p>
              </div>
              <button 
                class="btn btn-icon-only btn-danger" 
                type="button" 
                (click)="removeDrip(drip.id)" 
                *ngIf="drips.length > 1"
                title="Remove Email"
              >
                <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"></path>
                </svg>
              </button>
            </div>

            <div class="workflow-content">
              <div class="workflow-form-grid">
                <div class="form-group" *ngIf="index > 0">
                  <label class="form-label">
                    <svg class="label-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"></path>
                    </svg>
                    Wait Time
                  </label>
                  <select [ngModel]="drip.interval" (ngModelChange)="updateDripInterval(drip.id, $event)" class="form-select">
                    <option *ngFor="let interval of timeIntervals" [ngValue]="interval.value">
                      {{ interval.label }}
                    </option>
                  </select>
                </div>

                <div class="form-group" [ngClass]="{ 'full-width': index === 0 }">
                  <label class="form-label">
                    <svg class="label-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
                    </svg>
                    Email Template
                  </label>
                  <select [ngModel]="drip.template" (ngModelChange)="updateDripTemplate(drip.id, $event)" class="form-select">
                    <option value="">Select template...</option>
                    <option *ngFor="let template of emailTemplates" [ngValue]="template.id">
                      {{ template.name }}
                    </option>
                  </select>
                </div>
              </div>

              <div class="template-preview" *ngIf="drip.template">
                <div class="preview-header">
                  <div class="preview-title">
                    <svg class="preview-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
                    </svg>
                    {{ getTemplateName(drip.template) }}
                  </div>
                  <button type="button" class="btn btn-outline btn-sm" (click)="previewTemplate(drip.template)">
                    <svg class="btn-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"></path>
                      <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z"></path>
                    </svg>
                    Preview
                  </button>
                </div>
                <p class="preview-description">{{ getTemplateDescription(drip.template) }}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

  <!-- Templates Modal -->
  <div class="modal-overlay" *ngIf="showTemplateModal" (click)="closeTemplateModal()">
    <div class="modal-container" (click)="$event.stopPropagation()">
      <div class="modal-header">
        <div class="modal-title">
          <svg class="modal-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
          </svg>
          Email Templates
        </div>
        <button type="button" class="btn btn-icon-only btn-ghost" (click)="closeTemplateModal()" title="Close">
          <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"></path>
          </svg>
        </button>
      </div>
      <div class="modal-body">
        <div class="templates-grid">
          <div class="template-item" *ngFor="let template of emailTemplates">
            <div class="template-icon">
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
              </svg>
            </div>
            <div class="template-content">
              <h4 class="template-name">{{ template.name }}</h4>
              <p class="template-desc">{{ template.description }}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

  <!-- Create Template Modal -->
  <div class="modal-overlay" *ngIf="showCreateModal" (click)="closeCreateModal()">
    <div class="modal-container" (click)="$event.stopPropagation()">
      <div class="modal-header">
        <div class="modal-title">
          <svg class="modal-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6"></path>
          </svg>
          Create New Template
        </div>
        <button type="button" class="btn btn-icon-only btn-ghost" (click)="closeCreateModal()" title="Close">
          <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"></path>
          </svg>
        </button>
      </div>
      <div class="modal-body">
        <div class="form-section">
          <div class="form-group">
            <label for="templateName" class="form-label">
              <svg class="label-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M7 7h.01M7 3h5c.512 0 1.024.195 1.414.586l7 7a2 2 0 010 2.828l-7 7a2 2 0 01-2.828 0l-7-7A1.994 1.994 0 013 12V7a4 4 0 014-4z"></path>
              </svg>
              Template Name
            </label>
            <input 
              id="templateName" 
              type="text" 
              [(ngModel)]="templateForm.name" 
              placeholder="e.g., Product Launch Email" 
              class="form-input"
            />
          </div>
          <div class="form-group">
            <label for="templateDescription" class="form-label">
              <svg class="label-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
              </svg>
              Description
            </label>
            <input
              id="templateDescription"
              type="text"
              [(ngModel)]="templateForm.description"
              placeholder="Brief description of this template"
              class="form-input"
            />
          </div>
          <div class="form-group">
            <label for="templateContent" class="form-label">
              <svg class="label-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
              </svg>
              Email Content
            </label>
            <textarea
              id="templateContent"
              rows="6"
              [(ngModel)]="templateForm.content"
              placeholder="Write your email content here..."
              class="form-textarea"
            ></textarea>
          </div>
        </div>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-outline" (click)="closeCreateModal()">Cancel</button>
        <button type="button" class="btn btn-primary" (click)="createTemplate()">
          <svg class="btn-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 13l4 4L19 7"></path>
          </svg>
          Create Template
        </button>
      </div>
    </div>
  </div>

  <!-- Preview Modal -->
  <div class="modal-overlay" *ngIf="selectedTemplatePreview as templateId" (click)="closePreview()">
    <div class="modal-container modal-large" (click)="$event.stopPropagation()">
      <div class="modal-header">
        <div class="modal-title">
          <svg class="modal-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"></path>
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z"></path>
          </svg>
          {{ getTemplateName(templateId) }}
        </div>
        <button type="button" class="btn btn-icon-only btn-ghost" (click)="closePreview()" title="Close">
          <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"></path>
          </svg>
        </button>
      </div>
      <div class="modal-body">
        <div class="preview-section">
          <div class="preview-header-info">
            <h4 class="preview-subtitle">{{ getTemplateDescription(templateId) }}</h4>
          </div>
          <div class="preview-content">
            <div class="preview-text">
              {{ getTemplateContent(templateId) || 'Template content preview not available.' }}
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
