export class CampaignComponent {
  constructor(crmService, router) {
    this.crmService = crmService;
    this.router = router;
    this.title = 'Drip Campaign Builder';
    this.emailTemplates = [];
    this.timeIntervals = [];
    this.numDrips = 3;
    this.drips = [{
      id: 1,
      template: '',
      interval: 0,
      customInterval: 1
    }, {
      id: 2,
      template: '',
      interval: 3,
      customInterval: 3
    }, {
      id: 3,
      template: '',
      interval: 7,
      customInterval: 7
    }];
    this.globalInterval = 3;
    this.campaignName = '';
    this.showTemplateModal = false;
    this.showCreateModal = false;
    this.selectedTemplatePreview = null;
    this.templateForm = {
      name: '',
      description: '',
      content: ''
    };
    this.statusMessage = null;
    this.statusType = null;
    this.isSaving = false;
  }
  ngOnInit() {
    this.fetchTemplates();
    this.fetchTimeIntervals();
  }
  get totalDays() {
    return this.drips.reduce((sum, drip) => sum + (drip.id === 1 ? 0 : drip.interval), 0);
  }
  get configuredDrips() {
    return this.drips.filter(drip => Boolean(drip.template)).length;
  }
  fetchTemplates() {
    this.crmService.getTemplates().subscribe({
      next: templates => {
        this.emailTemplates = templates;
      },
      error: () => {
        // Fallback to local defaults if API is unavailable
        this.emailTemplates = [{
          id: 'welcome',
          name: 'Welcome Email',
          description: 'Introduce your brand',
          content: ''
        }, {
          id: 'intro',
          name: 'Product Introduction',
          description: 'Showcase your product',
          content: ''
        }, {
          id: 'case_study',
          name: 'Case Study',
          description: 'Share success stories',
          content: ''
        }];
      }
    });
  }
  fetchTimeIntervals() {
    this.crmService.getTimeIntervals().subscribe({
      next: intervals => {
        this.timeIntervals = intervals;
      },
      error: () => {
        this.timeIntervals = [{
          value: 1,
          label: '1 day'
        }, {
          value: 3,
          label: '3 days'
        }, {
          value: 7,
          label: '1 week'
        }];
      }
    });
  }
  handleDripCountChange(value) {
    const newCount = Math.max(1, Number(value) || 1);
    this.numDrips = newCount;
    if (newCount > this.drips.length) {
      const newDrips = [...this.drips];
      for (let i = this.drips.length; i < newCount; i += 1) {
        newDrips.push({
          id: i + 1,
          template: '',
          interval: i === 0 ? 0 : this.globalInterval,
          customInterval: this.globalInterval
        });
      }
      this.drips = newDrips;
    } else if (newCount < this.drips.length) {
      this.drips = this.drips.slice(0, newCount).map((drip, index) => ({
        ...drip,
        id: index + 1
      }));
    }
  }
  updateDripTemplate(id, templateId) {
    this.drips = this.drips.map(drip => drip.id === id ? {
      ...drip,
      template: templateId
    } : drip);
  }
  updateDripInterval(id, interval) {
    this.drips = this.drips.map(drip => drip.id === id ? {
      ...drip,
      interval,
      customInterval: interval
    } : drip);
  }
  addDrip() {
    const nextId = this.drips.length + 1;
    this.drips = [...this.drips, {
      id: nextId,
      template: '',
      interval: this.globalInterval,
      customInterval: this.globalInterval
    }];
    this.numDrips = nextId;
  }
  removeDrip(id) {
    if (this.drips.length <= 1) {
      return;
    }
    this.drips = this.drips.filter(drip => drip.id !== id).map((drip, index) => ({
      ...drip,
      id: index + 1
    }));
    this.numDrips = this.drips.length;
  }
  applyGlobalInterval() {
