      styles: [".page[_ngcontent-%COMP%] {\n  min-height: 100vh;\n  padding: 1.5rem;\n  background: #f3f4f6;\n}\n\n.header[_ngcontent-%COMP%] {\n  margin-bottom: 1.5rem;\n}\n\n.action-buttons[_ngcontent-%COMP%] {\n  display: flex;\n  gap: 0.75rem;\n  flex-wrap: wrap;\n}\n\n.status[_ngcontent-%COMP%] {\n  margin-top: 1rem;\n  padding: 0.75rem 1rem;\n  border-radius: 0.75rem;\n  font-weight: 500;\n}\n\n.status.success[_ngcontent-%COMP%] {\n  background: #dcfce7;\n  color: #166534;\n}\n\n.status.error[_ngcontent-%COMP%] {\n  background: #fee2e2;\n  color: #b91c1c;\n}\n\n.form-group[_ngcontent-%COMP%] {\n  margin-bottom: 1.25rem;\n}\n\n.form-group[_ngcontent-%COMP%]   label[_ngcontent-%COMP%] {\n  display: block;\n  font-size: 0.9rem;\n  font-weight: 600;\n  margin-bottom: 0.4rem;\n  color: #1f2937;\n}\n\n.inline-group[_ngcontent-%COMP%] {\n  display: flex;\n  gap: 0.75rem;\n}\n\n.inline-group[_ngcontent-%COMP%]   input[_ngcontent-%COMP%] {\n  flex: 1;\n}\n\n.info-box[_ngcontent-%COMP%] {\n  background: #eff6ff;\n  border: 1px solid #bfdbfe;\n  border-radius: 0.75rem;\n  padding: 1rem;\n}\n\n.info-box[_ngcontent-%COMP%]   select[_ngcontent-%COMP%] {\n  margin-bottom: 0.75rem;\n}\n\n.help-text[_ngcontent-%COMP%] {\n  margin: 0;\n  font-size: 0.75rem;\n  color: #4b5563;\n}\n\n.full[_ngcontent-%COMP%] {\n  width: 100%;\n}\n\n.workflow-list[_ngcontent-%COMP%] {\n  display: flex;\n  flex-direction: column;\n  gap: 1rem;\n}\n\n.workflow-wrapper[_ngcontent-%COMP%] {\n  position: relative;\n}\n\n.full-span[_ngcontent-%COMP%] {\n  grid-column: span 2;\n}\n\n.preview-header[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: space-between;\n  align-items: center;\n  font-size: 0.85rem;\n}\n\n.link[_ngcontent-%COMP%] {\n  background: none;\n  border: none;\n  color: #2563eb;\n  font-size: 0.85rem;\n  font-weight: 600;\n  padding: 0;\n}\n\n.modal-header[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: space-between;\n  align-items: center;\n  padding: 1.25rem 1.5re